mapper.select:
SELECT m.MENU_ID AS id, m.MENU_PARENT_ID AS pId, m.MENU_NAME AS title, m.MENU_LEVELS AS menuLevels, m.MENU_TYPE AS spread, m.MENU_PATH AS href, m.FLAG, m.DESC_CONTENT AS descContent, m.STATE AS state FROM sys_menu AS m LEFT JOIN sys_role_menu_rel AS rm ON m.MENU_ID = rm.MENU_ID WHERE m.STATE=1 and rm.state=1 and m.MENU_LEVELS > 1
AND rm.ROLE_ID = #{roleId,jdbcType=BIGINT}
group by m.MENU_ID ORDER BY m.FLAG

SELECT m.MENU_ID, m.MENU_PARENT_ID, m.MENU_NAME, m.MENU_LEVELS, m.MENU_TYPE, m.MENU_PATH, m.DESC_CONTENT, m.STATE, m.IS_PUBLISH, m.CREATED_DATE FROM sys_menu AS m LEFT JOIN sys_role_menu_rel AS rm ON m.MENU_ID = rm.MENU_ID WHERE STATE=1
AND rm.ROLE_ID = #{roleId,jdbcType=BIGINT}
group by m.MENU_ID

mapper.delete:
    delete from sys_role_menu_rel
   	 where

		MENU_ID=#{menuId} and ROLE_ID=#{roleId}
