mapper.insert:
insert into sys_user (USER_ID, ROLE_ID, USER_NAME, 
  USER_PASSWORD, USER_CH_NAME, USER_SEX, 
  STATE, END_IP, CREATED_DATE, 
  UPDATE_DATE, UPDATE_USER, CREATED_USER
  )
values (#{userId,jdbcType=BIGINT}, #{roleId,jdbcType=BIGINT}, #{userName,jdbcType=CHAR}, 
  #{userPassword,jdbcType=CHAR}, #{userChName,jdbcType=CHAR}, #{userSex,jdbcType=SMALLINT}, 
  #{state,jdbcType=SMALLINT}, #{endIp,jdbcType=CHAR}, #{createdDate,jdbcType=TIMESTAMP}, 
  #{updateDate,jdbcType=TIMESTAMP}, #{updateUser,jdbcType=CHAR}, #{createdUser,jdbcType=CHAR}
  )

mapper.select:
select
from sys_user where 1=1
AND USER_NAME=#{userName,jdbcType=VARCHAR}

mapper.update:
  	UPDATE sys_user

		GOOGLECODE_STATE =(case when GOOGLECODE_STATE=1 then 0 when GOOGLECODE_STATE=0 then 1 end)

		,GOOGLECODE_SECRET = #{googleSecret}


	WHERE
		USER_NAME = #{userName}
